@tailwind base;
@tailwind components;
@tailwind utilities;

/* Definition of the design system. All colors, gradients, fonts, etc should be defined here. 
All colors MUST be HSL.
*/

@layer base {
  :root {
    --background: 40 40% 97%;
    --foreground: 120 15% 25%;

    --card: 0 0% 100%;
    --card-foreground: 120 15% 25%;

    --popover: 0 0% 100%;
    --popover-foreground: 120 15% 25%;

    --primary: 100 15% 49%;
    --primary-foreground: 0 0% 100%;

    --secondary: 25 50% 88%;
    --secondary-foreground: 120 15% 25%;

    --muted: 40 40% 96%;
    --muted-foreground: 120 10% 45%;

    --accent: 25 50% 88%;
    --accent-foreground: 120 15% 25%;

    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 0 0% 100%;

    --border: 40 30% 90%;
    --input: 40 30% 90%;
    --ring: 100 15% 49%;

    --radius: 1.5rem;
    
    /* Pilates Studio Custom Colors */
    --sage: 100 15% 49%;
    --sage-foreground: 0 0% 100%;
    --peach: 25 50% 88%;
    --peach-foreground: 120 15% 25%;
    --cream: 40 40% 97%;
    --cream-foreground: 120 15% 25%;
    
    /* Shadows */
    --shadow-soft: 0 4px 20px rgba(0, 0, 0, 0.06);
    --shadow-card: 0 2px 12px rgba(0, 0, 0, 0.08);
    
    /* Transitions */
    --transition-smooth: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);

    --sidebar-background: 0 0% 98%;

    --sidebar-foreground: 240 5.3% 26.1%;

    --sidebar-primary: 240 5.9% 10%;

    --sidebar-primary-foreground: 0 0% 98%;

    --sidebar-accent: 240 4.8% 95.9%;

    --sidebar-accent-foreground: 240 5.9% 10%;

    --sidebar-border: 220 13% 91%;

    --sidebar-ring: 217.2 91.2% 59.8%;
  }

  .dark {
    --background: 120 15% 15%;
    --foreground: 40 40% 97%;

    --card: 120 15% 18%;
    --card-foreground: 40 40% 97%;

    --popover: 120 15% 18%;
    --popover-foreground: 40 40% 97%;

    --primary: 100 15% 49%;
    --primary-foreground: 0 0% 100%;

    --secondary: 120 15% 22%;
    --secondary-foreground: 40 40% 97%;

    --muted: 120 15% 22%;
    --muted-foreground: 40 20% 70%;

    --accent: 100 20% 35%;
    --accent-foreground: 40 40% 97%;

    --destructive: 0 62.8% 40.6%;
    --destructive-foreground: 40 40% 97%;

    --border: 120 15% 25%;
    --input: 120 15% 25%;
    --ring: 100 15% 60%;
    
    --sage: 100 15% 49%;
    --sage-foreground: 0 0% 100%;
    --peach: 25 30% 60%;
    --peach-foreground: 120 15% 15%;
    --cream: 40 20% 30%;
    --cream-foreground: 40 40% 97%;
    --sidebar-background: 240 5.9% 10%;
    --sidebar-foreground: 240 4.8% 95.9%;
    --sidebar-primary: 224.3 76.3% 48%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 240 3.7% 15.9%;
    --sidebar-accent-foreground: 240 4.8% 95.9%;
    --sidebar-border: 240 3.7% 15.9%;
    --sidebar-ring: 217.2 91.2% 59.8%;
  }
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground font-sans;
  }
  
  h1, h2, h3, h4, h5, h6 {
    @apply font-serif;
  }
}

@layer utilities {
  .font-serif {
    font-family: 'Playfair Display', serif;
  }
  
  .font-sans {
    font-family: 'Inter', sans-serif;
  }
  
  .section-padding {
    @apply py-16 md:py-24 lg:py-32;
  }
  
  .container-custom {
    @apply container mx-auto px-6 md:px-8 lg:px-12;
  }
  
  .btn-sage {
    @apply bg-sage text-sage-foreground hover:opacity-90 transition-all duration-300 rounded-full px-8 py-3 font-medium shadow-md hover:shadow-lg;
  }
  
  .card-hover {
    @apply transition-all duration-300 hover:shadow-lg hover:-translate-y-1;
  }
  
  /* Premium Animation Utilities */
  .animate-fade-in {
    animation: fadeIn 0.8s cubic-bezier(0.25, 0.46, 0.45, 0.94) forwards;
  }
  
  .animate-slide-up {
    animation: slideUp 0.8s cubic-bezier(0.25, 0.46, 0.45, 0.94) forwards;
  }
  
  .animate-zoom-in {
    animation: zoomIn 0.6s cubic-bezier(0.25, 0.46, 0.45, 0.94) forwards;
  }
  
  .animate-letter-spacing {
    animation: letterSpacing 1s cubic-bezier(0.25, 0.46, 0.45, 0.94) forwards;
  }
  
  .hover-lift {
    @apply transition-all duration-500 ease-out;
  }
  
  .hover-lift:hover {
    @apply -translate-y-2 shadow-2xl;
  }
  
  .button-ripple {
    @apply relative overflow-hidden;
  }
  
  .button-ripple::before {
    content: '';
    @apply absolute inset-0 bg-white/20 scale-0 rounded-full;
    transition: transform 0.6s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  }
  
  .button-ripple:hover::before {
    @apply scale-150;
  }
  
  .input-focus-animation {
    @apply transition-all duration-300 ease-out;
  }
  
  .input-focus-animation:focus {
    @apply scale-[1.02] shadow-lg;
  }
}

@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(40px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes zoomIn {
  from {
    opacity: 0;
    transform: scale(0.95);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes letterSpacing {
  from {
    letter-spacing: 0.1em;
    opacity: 0;
  }
  to {
    letter-spacing: normal;
    opacity: 1;
  }
}

@keyframes pulse-subtle {
  0%, 100% {
    opacity: 1;
    transform: scale(1);
  }
  50% {
    opacity: 0.95;
    transform: scale(1.02);
  }
}

@keyframes shimmer {
  0% {
    background-position: -200% 0;
  }
  100% {
    background-position: 200% 0;
  }
}

.pulse-subtle {
  animation: pulse-subtle 3s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

.shimmer {
  background: linear-gradient(
    90deg,
    transparent 0%,
    rgba(255, 255, 255, 0.1) 50%,
    transparent 100%
  );
  background-size: 200% 100%;
  animation: shimmer 3s infinite;
}

/* Smooth scroll behavior */
html {
  scroll-behavior: smooth;
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 10px;
}

